    // Do something with the form values.
    // ✅ This will be type-safe and validated.
    console.log(values);

    addChain({
      name: values.name,
      chainId: Number(values.chainId),
      rpcUrl: values.rpcUrl,
      blockExplorerUrl: values.blockExplorerUrl,
    });
